"""
  Dummy of nose, to test that loading nose from virtualenv works.

"""

import sys

def run_exit(*args, **kwargs):
    for arg in sys.argv[1:]:
      if not '=' in arg:
        continue
      command, target = arg.split('=', 1)
      if command == '--xunit-file':
        with open(target, 'w') as fh:
          fh.write('Printed from external nose')
